DROP TABLE IF EXISTS `article14`;
CREATE TABLE `article14` (
  `unid` varchar(50) NOT NULL,
  `title` varchar(500) NOT NULL,
  `abstract` text NOT NULL,
  `content` text NOT NULL,
  `author` varchar(30) NOT NULL,
  `date` datetime NOT NULL,
